import React, { useState } from 'react';
import { navigate } from 'react-router-dom';
import './Button.css' 

function HomePage() {
    const [userInput, setUserInput] = useState('');

    /////////////////////////////////////////////////////////
    //HELPER FUNCTIONS //////////////////////////////////////
    /////////////////////////////////////////////////////////
    const handleInputChange = (event) => {
        setUserInput(event.target.value);
    }

    const goToSignInPage = () => {
        navigate('/signin');
    }
    /////////////////////////////////////////////////////////
    /////////////////////////////////////////////////////////
    
    return (
    <div style={{ backgroundColor: 'lightBlue', height: '100vh', 
    textAlign: 'left', padding: '10px' }}>
      <h1>Bruin Referrals</h1>
      <h2><button className="styled-button" onClick={goToSignInPage}>Sign In</button></h2>
      <p>This is a basic homepage created with React.</p>

      {/* Input field for user to type in */}
      <input
        type="text"
        placeholder="Placeholder for Search Bar"
        value={userInput}
        onChange={handleInputChange}
        style={{ padding: '10px', fontSize: '16px' }}
      />

      {/* Display the user's input */}
      <p>You typed: {userInput}</p>

      <button onClick={() => alert('Button Clicked!')}>Click Me</button>
    </div>
  );
}

export default HomePage;
